protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        int[] repeatTimes = new int[TotalAdInsured];\n        myRepeater.DataSource = repeatTimes;\n        myRepeater.DataBind();\n    }\n}\n\nprotected void myRepeater_ItemDataBound(Object Sender, RepeaterItemEventArgs e)\n{\n    // Ensure we are dealing with an item (not header/footer etc.)\n    if (e.Item.ItemType == ListItemType.Item || e.Item.ItemType == ListItemType.AlternatingItem)\n    {\n        DropDownList ddlAdTitle = (DropDownList)e.Item.FindControl("AdTitle");\n        if (ddlAdTitle != null)\n        {\n            // Set a unique ID for each dropdown\n            ddlAdTitle.ID = "AdTitle" + e.Item.ItemIndex.ToString();\n        }\n    }\n}