using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example string with environment variable\n        string pathWithEnvVar = @"%programfiles%\directory\tool.exe";\n\n        // Expand the environment variable to get the full path\n        string fullPath = Environment.ExpandEnvironmentVariables(pathWithEnvVar);\n\n        try\n        {\n            // Start the process\n            Process.Start(fullPath);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}