using System;\nusing Microsoft.Win32;\n\nclass Program\n{\n    static void Main()\n    {\n        string providerName = "Microsoft.ACE.OLEDB.12.0";\n        if (IsOleDbProviderInstalled(providerName))\n            Console.WriteLine($"{providerName} is installed.");\n        else\n            Console.WriteLine($"{providerName} is not installed.");\n    }\n\n    static bool IsOleDbProviderInstalled(string providerName)\n    {\n        string key = $"SOFTWARE\\Classes\\CLSID\\{{{GetOleDBProviderGuid(providerName)}}}";\n        return Registry.GetValue(key, null, null) != null;\n    }\n\n    static string GetOleDBProviderGuid(string providerName)\n    {\n        // This is a mock method. In practice, you should look up the CLSID\n        // for your specific OLE DB provider.\n        switch (providerName)\n        {\n            case "Microsoft.ACE.OLEDB.12.0":\n                return "EAB0CF75-6F83-4A99-B5D9-DA5E8B87EEC4";\n            default:\n                throw new ArgumentException("Provider not recognized.");\n        }\n    }\n}