// Clear all session variables\nSession.Clear();\n\n// Abandon the session\nSession.Abandon();\n\n// Optionally, you can invalidate and remove the server-side session state\nHttpContext.Current.Session = null;\n\n// Remove the ASP.NET Session Cookie\nHttpCookie cookie = new HttpCookie("ASP.NET_SessionId", "")\n{\n    Expires = DateTime.Now.AddYears(-1),\n    Domain = Request.Url.Host // Important if you are working with subdomains\n};\n\nResponse.Cookies.Add(cookie);\n\n// Optionally end the response after abandoning the session\n// Note: Using Response.End() can cause issues and it's generally better to redirect\n// Response.End();\n\n// Redirect to a fresh page (e.g., login page)\nResponse.Redirect("~/Login.aspx", true);