using System.Security.Cryptography;\n\nclass Program\n{\n    static void Main()\n    {\n        // Specify the key size (e.g., 2048 bits)\n        int keySize = 2048;\n\n        // Create a new instance of RSACryptoServiceProvider with the specified key size\n        using (RSACryptoServiceProvider rsaProvider = new RSACryptoServiceProvider(keySize))\n        {\n            // Optionally, you can retrieve and use the public/private keys as needed\n            string publicKey = rsaProvider.ToXmlString(false);\n            string privateKey = rsaProvider.ToXmlString(true);\n\n            System.Console.WriteLine("Public Key: " + publicKey);\n            System.Console.WriteLine("Private Key: " + privateKey);\n        }\n    }\n}