using (var db = new YourDataContext())\n{\n    var query = from d in db.dateTable\n                where d.category == "someCat"\n                from tvfResult in db.tvFunction(d.dt, "anotherParam")\n                select new { d.dateCol, tvfResult.descr, tvfResult.value };\n\n    foreach (var result in query)\n    {\n        Console.WriteLine($"{result.dateCol} - {result.descr}: {result.value}");\n    }\n}