using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class DataProtectorExample\n{\n    public static byte[] ProtectData(string data)\n    {\n        return ProtectedData.Protect(Encoding.UTF8.GetBytes(data), null, Scope.CurrentUser);\n    }\n\n    public static string UnprotectData(byte[] protectedBytes)\n    {\n        var unprotectedBytes = ProtectedData.Unprotect(protectedBytes, null, Scope.CurrentUser);\n        return Encoding.UTF8.GetString(unprotectedBytes);\n    }\n}