using System;\nusing System.IO;\nusing System.Net;\n\nclass FtpClientHelper\n{\n    private readonly string _ftpServerUrl;\n    private readonly NetworkCredential _credentials;\n\n    public FtpClientHelper(string serverUrl, NetworkCredential credentials)\n    {\n        _ftpServerUrl = serverUrl;\n        _credentials = credentials;\n    }\n\n    // Check if a directory exists on the FTP server.\n    public bool DirectoryExists(string remoteFolderName)\n    {\n        using (var request = (FtpWebRequest)WebRequest.Create($"{_ftpServerUrl}/{remoteFolderName}"))\n        {\n            request.Method = WebRequestMethods.Ftp.ListDirectory;\n            request.Credentials = _credentials;\n\n            try\n            {\n                using (request.GetResponse())\n                    return true; // Folder found.\n            }\n            catch (WebException ex)\n            {\n                var response = (FtpWebResponse)ex.Response;\n                if (response.StatusCode == FtpStatusCode.ActionNotTakenFileUnavailable ||\n                    response.StatusCode == FtpStatusCode.FileActionAbortedNoTransferStarted)\n                    return false; // Folder does not exist.\n\n                // Handle other WebException cases as necessary.\n            }\n        }\n\n        return false;\n    }\n\n    // Create a directory on the FTP server.\n    public bool CreateDirectory(string remoteFolderName)\n    {\n        using (var request = (FtpWebRequest)WebRequest.Create($"{_ftpServerUrl}/{remoteFolderName}"))\n        {\n            request.Method = WebRequestMethods.Ftp.MakeDirectory;\n            request.Credentials = _credentials;\n\n            try\n            {\n                using (request.GetResponse())\n                    return true; // Folder created successfully.\n            }\n            catch (WebException)\n            {\n                // Handle the exception as necessary, perhaps directory already exists.\n                // For simplicity, we consider this as false.\n                return false;\n            }\n        }\n    }\n\n    static void Main(string[] args)\n    {\n        var ftpUrl = "ftp://your-ftp-server";\n        var credentials = new NetworkCredential("username", "password");\n\n        var ftpClientHelper = new FtpClientHelper(ftpUrl, credentials);\n\n        string folderName = "TestFolder";\n\n        if (!ftpClientHelper.DirectoryExists(folderName))\n        {\n            bool isCreated = ftpClientHelper.CreateDirectory(folderName);\n            Console.WriteLine($"Directory '{folderName}' {(isCreated ? "created" : "not created")}.");\n        }\n        else\n        {\n            Console.WriteLine($"Directory '{folderName}' already exists.");\n        }\n    }\n}