using Ionic.Zip;\n\n// Add DotNetZip to your project via NuGet package manager:\n// Install-Package DotNetZip\n\npublic void CreateAESZippedFile()\n{\n    var password = "your-password";\n    using (var zip = new ZipFile())\n    {\n        zip.AddDirectory("path-to-folder");\n        zip.Password = password;\n        zip.Encryption = EncryptionAlgorithm.WinZipAes256; // AES-256\n        zip.Save("archive.zip");\n    }\n}