using System;\nusing System.Net;\nusing System.Net.Http;\nusing System.Xml;\n\nclass Program\n{\n    static async System.Threading.Tasks.Task Main(string[] args)\n    {\n        string url = "http://example.com/path/to/your/xml";\n        XmlDocument xmlDoc = new XmlDocument();\n\n        // Set up the HttpClientHandler to use a proxy\n        var handler = new HttpClientHandler()\n        {\n            Proxy = new WebProxy("http://your_proxy_address:port", true),\n            UseCookies = false\n        };\n\n        using (var client = new HttpClient(handler))\n        {\n            try\n            {\n                string xmlData = await client.GetStringAsync(url);\n                xmlDoc.LoadXml(xmlData);\n\n                // Use xmlDoc as needed\n                Console.WriteLine(xmlDoc.OuterXml);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n    }\n}