using System;\nusing iTextSharp.text.pdf;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Path to your template PDF form and output file\n        string pdfTemplate = "template.pdf";\n        string filledPdf = "filled_template.pdf";\n\n        // Open the PDF form\n        using (PdfReader reader = new PdfReader(pdfTemplate))\n        {\n            // Create a memory stream\n            using (MemoryStream msOutput = new MemoryStream())\n            {\n                // Create a stamper to add content to the existing PDF\n                using (PdfStamper stamper = new PdfStamper(reader, msOutput))\n                {\n                    // Fill in a text field\n                    var fields = stamper.AcroFields;\n                    fields.SetField("fieldName", "value");\n\n                    // If you have checkboxes/radio buttons to check/uncheck\n                    // Use the SetField method with options like "Yes" or "Off"\n                    fields.SetField("checkBoxFieldName", "Yes");\n                }\n\n                // Write output file\n                File.WriteAllBytes(filledPdf, msOutput.ToArray());\n            }\n        }\n    }\n}