// In your Windows Service using SignalR client side\npublic class DataService : HubConnectionBuilder, IHostedService\n{\n    private HubConnection _hubConnection;\n\n    public async Task StartAsync(CancellationToken cancellationToken)\n    {\n        _hubConnection = new HubConnectionBuilder()\n            .WithUrl("https://yourwebapiurl/api/datareloadsighub")\n            .Build();\n\n        _hubConnection.On("ReloadData", () =>\n        {\n            // Your code to reload data\n            ReloadDatabaseData();\n        });\n\n        await _hubConnection.StartAsync(cancellationToken);\n    }\n\n    private void ReloadDatabaseData()\n    {\n        // Logic to reload data from the database\n    }\n\n    public async Task StopAsync(CancellationToken cancellationToken)\n    {\n        if (_hubConnection is { State: HubConnectionState.Connected })\n        {\n            await _hubConnection.StopAsync();\n        }\n    }\n}