using (var db = Db4oEmbedded.OpenFile("yourDatabaseFile.db4o"))\n{\n    int n = 5; // number of records to retrieve\n    var topNObjects = from MyObject obj in db \n                      select obj \n                      into queryableObjects \n                      select queryableObjects.Skip(0).Take(n);\n\n    foreach (var obj in topNObjects)\n    {\n        Console.WriteLine(obj);\n    }\n}