protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        StringBuilder sb = new StringBuilder();\n        \n        // Example data\n        List<string[]> dataRows = GetTableData();\n\n        foreach (string[] row in dataRows)\n        {\n            sb.Append("<tr>");\n            foreach (string cellData in row)\n            {\n                sb.AppendFormat("<td>{0}</td>", HttpUtility.HtmlEncode(cellData));\n            }\n            sb.Append("</tr>");\n        }\n\n        ltlTableRows.Text = sb.ToString();\n    }\n}\n\nprivate List<string[]> GetTableData()\n{\n    // Method to retrieve the data as a list of string arrays\n    return new List<string[]>\n    {\n        new[] { "Data1_1", "Data1_2" },\n        new[] { "Data2_1", "Data2_2" }\n        // ... other rows ...\n    };\n}