// Example WCF Service Contract Interface\n   [ServiceContract]\n   public interface IComApiBridge\n   {\n       [OperationContract]\n       void InvokeComMethod();\n   }\n\n   // Implementation of the service contract using COM Interop\n   public class ComApiBridge : IComApiBridge\n   {\n       public void InvokeComMethod()\n       {\n           var comObject = new MyComLibrary.MyComClass();\n           comObject.DoWork();\n       }\n   }