using System.Diagnostics;\nusing System.IO.Pipes;\nusing System.Threading;\n\nclass ParentApplication\n{\n    static void Main()\n    {\n        ProcessStartInfo psInfo = new ProcessStartInfo(@"MyApplication.exe");\n        psInfo.RedirectStandardOutput = true;\n        Process proc = Process.Start(psInfo);\n\n        Thread.Sleep(100); // Small delay to ensure the server is listening\n\n        using (NamedPipeClientStream pipeClient = new NamedPipeClientStream("myNamedPipe"))\n        {\n            try\n            {\n                Console.WriteLine("Attempting to connect to myNamedPipe...");\n                pipeClient.Connect();\n                Console.WriteLine("Connected.");\n\n                using (StreamReader reader = new StreamReader(pipeClient))\n                {\n                    string message = reader.ReadLine();\n\n                    if (message == "Ready")\n                    {\n                        Console.WriteLine("The launched application is fully loaded.");\n                        // Continue with your logic...\n                    }\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n\n            if (!proc.HasExited)\n            {\n                proc.CloseMainWindow();\n            }\n        }\n    }\n}