using System;\nusing System.Runtime.CompilerServices;\n\nclass Program\n{\n    static void Main()\n    {\n        Console.WriteLine("Running methods...");\n        MethodThatMightBeJitted();\n        MethodThatMightNotBeJitted();\n\n        // Force JIT compilation of a method if necessary\n        RuntimeHelpers.PrepareMethod(MethodBase.GetCurrentMethod().MethodHandle);\n    }\n\n    [MethodImpl(MethodImplOptions.NoInlining)]\n    static void MethodThatMightBeJitted()\n    {\n        Console.WriteLine("This method might be JIT compiled.");\n    }\n\n    [MethodImpl(MethodImplOptions.NoInlining)]\n    static void MethodThatMightNotBeJitted()\n    {\n        Console.WriteLine("This method might not be JIT compiled due to heuristics.");\n    }\n}