protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        BindGridView();\n    }\n}\n\nprivate void BindGridView()\n{\n    // Your code to fetch data from Oracle database and fill a DataTable or similar dataset\n    DataTable dataTable = GetDataFromOracle();\n\n    // Bind the data to the GridView\n    GridView.DataSource = dataTable;\n    GridView.DataBind();\n\n    // Check if the GridView is empty after binding\n    if (GridView.Rows.Count == 0)\n    {\n        // Do Something, e.g., display a message or hide the GridView\n        lblMessage.Text = "No records found.";\n        GridView.Visible = false;\n    }\n}\n\nprivate DataTable GetDataFromOracle()\n{\n    // Replace with your actual data fetching logic using OracleConnection and OracleDataAdapter\n    return new DataTable();\n}