public struct Temperature\n{\n    private double celsius;\n\n    public Temperature(double celsius)\n    {\n        this.celsius = celsius;\n    }\n\n    // Conversion from double (Fahrenheit) to Temperature (Celsius)\n    public static implicit operator Temperature(double fahrenheit)\n    {\n        return new Temperature((fahrenheit - 32) * 5 / 9);\n    }\n\n    // Conversion from Temperature (Celsius) to double (Fahrenheit)\n    public static explicit operator double(Temperature temp)\n    {\n        return (temp.celsius * 9 / 5) + 32;\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        double fahrenheit = 77.0;\n        Temperature tempC = fahrenheit; // Implicit conversion to Celsius\n\n        Console.WriteLine($"Temperature in Celsius: {tempC}");\n        \n        double convertedFahrenheit = (double)tempC; // Explicit conversion to Fahrenheit\n        \n        Console.WriteLine($"Converted temperature back to Fahrenheit: {convertedFahrenheit}");\n    }\n}