using System;\nusing System.Data;\nusing System.IO;\nusing System.Windows.Forms;\n\npublic class CsvMappingForm : Form {\n    private Button loadButton;\n    private Button importButton;\n    private ComboBox[] mappings;\n    private DataTable csvTable;\n    private string filePath;\n\n    public CsvMappingForm() {\n        InitializeComponents();\n    }\n\n    private void InitializeComponents() {\n        Text = "CSV Mapping Tool";\n        loadButton = new Button { Text = "Load CSV", AutoSize = true, Location = new System.Drawing.Point(10, 10) };\n        importButton = new Button { Text = "Import CSV", AutoSize = true, Location = new System.Drawing.Point(150, 10), Enabled = false };\n        loadButton.Click += LoadCsv;\n        importButton.Click += ImportToDatabase;\n\n        Controls.AddRange(new Control[] { loadButton, importButton });\n    }\n\n    private void LoadCsv(object sender, EventArgs e) {\n        OpenFileDialog openFileDialog = new OpenFileDialog();\n        if (openFileDialog.ShowDialog() == DialogResult.OK)\n            filePath = openFileDialog.FileName;\n\n        csvTable = ReadCsv(filePath);\n        mappings = new ComboBox[csvTable.Columns.Count];\n\n        int y = 40;\n        for (int i = 0; i < csvTable.Columns.Count; i++) {\n            Label label = new Label { Text = "CSV Column: " + csvTable.Columns[i].ColumnName, Location = new System.Drawing.Point(10, y) };\n            mappings[i] = new ComboBox { Location = new System.Drawing.Point(200, y), Size = new System.Drawing.Size(200, 25) };\n            foreach (string column in DatabaseColumns()) {\n                mappings[i].Items.Add(column);\n            }\n            Controls.AddRange(new Control[] { label, mappings[i] });\n            y += 30;\n        }\n\n        importButton.Enabled = true;\n    }\n\n    private DataTable ReadCsv(string filePath) {\n        DataTable dataTable = new DataTable();\n        using (var reader = new StreamReader(filePath)) {\n            string headerLine = reader.ReadLine();\n            string[] headers = headerLine.Split(',');\n            foreach (string header in headers)\n                dataTable.Columns.Add(header);\n\n            while (!reader.EndOfStream) {\n                var row = reader.ReadLine().Split(',');\n                dataTable.Rows.Add(row);\n            }\n        }\n\n        return dataTable;\n    }\n\n    private static string[] DatabaseColumns() {\n        // Replace with your database column names\n        return new[] { "Name", "Address", "Phone" };\n    }\n\n    private void ImportToDatabase(object sender, EventArgs e) {\n        DataTable mappedTable = new DataTable();\n        foreach (var mapping in mappings) {\n            string dbColumn = mapping.SelectedItem.ToString();\n            mappedTable.Columns.Add(dbColumn);\n        }\n\n        foreach (DataRow row in csvTable.Rows) {\n            DataRow newRow = mappedTable.NewRow();\n            for (int i = 0; i < mappings.Length; i++) {\n                var comboBoxItem = mappings[i].SelectedItem;\n                if (comboBoxItem != null)\n                    newRow[comboBoxItem.ToString()] = row[i];\n            }\n            mappedTable.Rows.Add(newRow);\n        }\n\n        // Implement your database import logic here\n    }\n\n    [STAThread]\n    static void Main() {\n        Application.EnableVisualStyles();\n        Application.Run(new CsvMappingForm());\n    }\n}