using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Text.Json;\nusing Microsoft.Win32;\n\npublic class Program\n{\n    private static readonly string AppDataPath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), "YourAppName");\n\n    public static void Main()\n    {\n        // Sample list to serialize\n        List<string> sampleList = new List<string> { "item1", "item2", "item3" };\n\n        // Serialize the list and save it to a file\n        string filePath = Path.Combine(AppDataPath, "data.json");\n        Directory.CreateDirectory(Path.GetDirectoryName(filePath));\n        JsonSerializerOptions options = new JsonSerializerOptions { WriteIndented = true };\n        string jsonString = JsonSerializer.Serialize(sampleList, options);\n        File.WriteAllText(filePath, jsonString);\n\n        // Deserialize the list from the file\n        string storedJsonString = File.ReadAllText(filePath);\n        List<string> retrievedList = JsonSerializer.Deserialize<List<string>>(storedJsonString);\n\n        foreach (var item in retrievedList)\n        {\n            Console.WriteLine(item); // Output: item1, item2, item3\n        }\n    }\n}