using System;\nusing iTextSharp.text.pdf;\n\npublic class PdfPageCounter\n{\n    public static int GetNumberOfPages(string path)\n    {\n        using (PdfReader pdf = new PdfReader(path))\n        {\n            return pdf.NumberOfPages;\n        }\n    }\n\n    public static void Main()\n    {\n        string filePath = @"C:\path\to\your\file.pdf";\n        int numberOfPages = GetNumberOfPages(filePath);\n        Console.WriteLine("Number of Pages: " + numberOfPages);\n    }\n}