using OfficeOpenXml;\n   using System.IO;\n\n   public void CreateExcelFile()\n   {\n       var newFileName = "MyExcel.xlsx";\n       FileInfo fileInfo = new FileInfo(newFileName);\n       \n       using (var package = new ExcelPackage(fileInfo))\n       {\n           // Add a worksheet to the empty workbook:\n           ExcelWorksheet worksheet = package.Workbook.Worksheets.Add("Sample Sheet");\n           \n           // Adding Header row\n           worksheet.Cells[1, 1].Value = "Header1";\n           worksheet.Cells[1, 2].Value = "Header2";  \n           \n           // Populate data rows\n           worksheet.Cells["A2"].LoadFromTexts(new[]\n           {\n               new[] { "Data1", "Data2" }\n           });\n           \n           package.Save();\n       }\n   }