public ActionResult YourAction()\n{\n    bool isMvcAjaxRequest = Request.IsAjaxRequest() && Request.Headers["RequestVia"] == "jQuery";\n\n    if (isMvcAjaxRequest)\n    {\n        // Return JSON result for AJAX request\n        return Json(new { success = true }, JsonRequestBehavior.AllowGet);\n    }\n    else\n    {\n        // Return view result for non-AJAX request\n        return View();\n    }\n}