using System;\nusing System.Data;\nusing Oracle.DataAccess.Client; // Make sure you have the correct Oracle data provider\n\npublic partial class YourPage : System.Web.UI.Page\n{\n    protected void LoadDataButton_Click(object sender, EventArgs e)\n    {\n        string connectionString = "YourConnectionStringHere"; // Replace with your connection string\n        using (OracleConnection conn = new OracleConnection(connectionString))\n        using (OracleCommand cmd = new OracleCommand("YourStoredProcedureName", conn))\n        {\n            cmd.CommandType = CommandType.StoredProcedure;\n\n            // Add any input parameters as necessary\n            //cmd.Parameters.Add(new OracleParameter("InputParam", OracleDbType.Varchar2)).Value = "example";\n\n            // Define the RefCursor parameter to receive data from the stored procedure\n            OracleParameter refCursorParam = new OracleParameter();\n            refCursorParam.ParameterName = "OutputRefCursor";\n            refCursorParam.Direction = ParameterDirection.Output;\n            refCursorParam.OracleDbType = OracleDbType.RefCursor;\n            cmd.Parameters.Add(refCursorParam);\n\n            conn.Open();\n            using (OracleDataReader reader = cmd.ExecuteReader())\n            {\n                DataTable dt = new DataTable();\n                dt.Load(reader);\n                GridView1.DataSource = dt;\n                GridView1.DataBind();\n            }\n        }\n    }\n}