private readonly Dictionary<string, IApplication> _cache = new Dictionary<string, IApplication>();\n\npublic IApplication GetApplication(string applicationName)\n{\n    if (_cache.TryGetValue(applicationName, out var cachedApp))\n        return cachedApp;\n\n    var appLazy = _applicationsWithMetadata.FirstOrDefault(a => a.Metadata.Application == applicationName);\n    if (appLazy != null)\n    {\n        var app = appLazy.Value;\n        _cache[applicationName] = app;\n        return app;\n    }\n\n    return null; // or throw an exception, depending on your requirements\n}