string[] lines = File.ReadAllLines(@"C:\Temp\Data.dat");\nconst int batchSize = 500; // Adjust batch size based on your testing\nint i = 0;\n\nwhile (i < lines.Length)\n{\n    using (var myDataContext = new MyDataContext())\n    {\n        myDataContext.ObjectTrackingEnabled = false; // Disable object tracking\n\n        for (int j = 0; j < batchSize && i < lines.Length; j++, i++)\n        {\n            string line = lines[i];\n            MyDataObject obj = ParseObject(line);\n            myDataContext.MyDataObjects.InsertOnSubmit(obj);\n        }\n\n        myDataContext.SubmitChanges();\n    }\n}