OracleCommand cmd = new OracleCommand("your_procedure", connection);\ncmd.CommandType = CommandType.StoredProcedure;\n\n// Assuming you have a list of ids\nList<int> ids = new List<int> { 1, 2, 3, 4 };\n\n// Create an OracleParameter for the collection\nOracleParameter param = cmd.Parameters.Add("ids", OracleDbType.Int64, ParameterDirection.Input);\nparam.UdtTypeName = "NUMBER_TABLE";\nparam.Value = ids.ToArray();\n\ncmd.ExecuteNonQuery();