using System;\nusing System.Globalization;\nusing System.IO;\nusing Gtk;\n\npublic class WelcomePage : ScrolledWindow\n{\n    private WebKit2.WebView webView;\n\n    public WelcomePage()\n    {\n        webView = new WebKit2.WebView();\n        Add(webView);\n        // Load HTML based on current culture\n        LoadWelcomeHTML();\n    }\n\n    private void LoadWelcomeHTML()\n    {\n        string langCode = CultureInfo.CurrentCulture.TwoLetterISOLanguageName;\n        string htmlPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "Resources", "html", langCode, "welcome.html");\n\n        if (!File.Exists(htmlPath))\n        {\n            // Fallback to default language, e.g., English\n            htmlPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "Resources", "html", "en", "welcome.html");\n        }\n\n        webView.LoadUri(new Uri($"file://{htmlPath}").ToString());\n    }\n}