using System;\nusing Chilkat;\n\nclass Program {\n    static void Main() {\n        string originalString = "Here is some data to encrypt!";\n        \n        Chilkat.Crypt2 crypt = new Chilkat.Crypt2();\n        crypt.EncodingMode = "hex";\n        bool success;\n        \n        crypt.CryptAlgorithm = "aes";\n        crypt.KeyLength = 256;\n        crypt.CipherMode = "cbc";\n        crypt.PaddingScheme = 0; // PKCS7\n        crypt.SecretKey = "0123456789abcdef0123456789abcdef"; // Must be 32 bytes for AES-256\n        \n        string encryptedString = crypt.encryptStringENC(originalString);\n        \n        if (crypt.LastMethodSuccess != true) {\n            Console.WriteLine(crypt.LastErrorText);\n            return;\n        }\n        \n        string decryptedString = crypt.decryptStringENC(encryptedString);\n        \n        Console.WriteLine($"Original: {originalString}");\n        Console.WriteLine($"Encrypted: {encryptedString}");\n        Console.WriteLine($"Decrypted: {decryptedString}");\n    }\n}