using System;\nusing System.Collections;\nusing System.Globalization;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string[] words = { "apple", "zebra", "yak" };\n\n        // Create a comparer using en-US culture\n        Comparer comparer = new CultureInfo("en-US").CompareInfo.GetStringComparer(StringComparison.CurrentCulture);\n\n        // Sort the array using the custom comparer\n        Array.Sort(words, (IComparer)comparer);\n        \n        foreach (string word in words)\n        {\n            Console.WriteLine(word);\n        }\n\n        // Search for an item using the same comparer\n        int index = Array.BinarySearch(words, "yak", (IComparer)comparer);\n\n        if (index >= 0)\n        {\n            Console.WriteLine("Found 'yak' at index: " + index);\n        }\n        else\n        {\n            Console.WriteLine("'yak' not found.");\n        }\n    }\n}