using Renci.SshNet;\n\nstring host = "your-sftp-server";\nstring username = "username";\nstring password = "password";\nint port = 22; // Default SFTP port\n\nusing (var client = new SftpClient(host, port, username, password))\n{\n    client.Connect();\n    using (var fileStream = File.OpenRead(@"local-file-path"))\n    {\n        client.UploadFile(fileStream, "/remote-directory/file.zip", true);\n    }\n    client.Disconnect();\n}