using System;\nusing System.Diagnostics;\nusing Microsoft.AspNetCore.Mvc;\n\npublic class LogsController : Controller\n{\n    [HttpGet]\n    public IActionResult ViewEventLog(string logName)\n    {\n        if (!User.IsInRole("Admin")) // Ensure user has admin role or permissions\n        {\n            return Forbid();\n        }\n\n        var eventLog = new EventLog(logName);\n        var entries = eventLog.Entries;\n\n        ViewBag.LogEntries = entries;\n        \n        return View();\n    }\n}