protected void Button1_Click(object sender, EventArgs e)\n{\n    List<Dictionary<string, string>> data = new List<Dictionary<string, string>>();\n\n    foreach (RepeaterItem item in myRepeater.Items)\n    {\n        if (item.ItemType == ListItemType.Item || item.ItemType == ListItemType.AlternatingItem)\n        {\n            DropDownList ddlAdTitle = (DropDownList)item.FindControl("AdTitle");\n            TextBox txtAdFullName = (TextBox)item.FindControl("AdFullName");\n\n            if (ddlAdTitle != null && txtAdFullName != null)\n            {\n                Dictionary<string, string> row = new Dictionary<string, string>\n                {\n                    { "AdTitle", ddlAdTitle.SelectedValue },\n                    { "AdFullName", txtAdFullName.Text }\n                };\n                data.Add(row);\n            }\n        }\n    }\n\n    // Now you can loop through the 'data' list and insert into your database\n}