[STAThread]\nstatic void Main(string[] args)\n{\n    bool restart = true;\n    while(restart)\n    {\n        using (var myWcfHost = new MyWcfHost())\n        {\n            myWcfHost.start();\n\n            Console.WriteLine("Press Enter to quit or Ctrl+R to restart");\n\n            restart = WaitForRestart();\n        }\n    }\n}\n\nprivate static bool WaitForRestart()\n{\n    while (true) // Use an infinite loop instead of recursion\n    {\n        // clear users input\n        Console.CursorLeft = 0;\n        Console.Write(" ");\n        Console.CursorLeft = 0;\n\n        // read users input\n        var key = Console.ReadKey();\n        if ((key.Modifiers & ConsoleModifiers.Control) != 0 && key.Key == ConsoleKey.R)\n        {\n            // refresh the settings\n            ConfigurationManager.RefreshSection("appSettings");\n            return true;\n        }\n        if (key.Key == ConsoleKey.Enter || key.Key == ConsoleKey.Escape)\n        {\n            return false;\n        }\n    }\n}