// This would be in your class library\npublic class MyClassLibrary\n{\n    public void PrintMessage(string message)\n    {\n        Console.WriteLine(message);\n    }\n}\n\n// This would be in your Console Application\nclass Program\n{\n    static void Main(string[] args)\n    {\n        MyClassLibrary myLib = new MyClassLibrary();\n        myLib.PrintMessage("Hello from the class library!");\n    }\n}