var result = from c in context.Collects\n             group c by new { c.Url, c.CollectId, c.UserId, c.PubTime } into g\n             where g.Key.PubTime == (from d in context.Collects\n                                     where d.Url == g.Key.Url\n                                     select d.PubTime).Max()\n             orderby g.Key.PubTime descending\n             select new { \n                 CollectId = g.Key.CollectId, \n                 Url = g.Key.Url, \n                 UserId = g.Key.UserId, \n                 PubTime = g.Key.PubTime \n             };