using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string path = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData), "MyApp", "data.txt");\n        \n        // Ensure the directory exists or create it\n        Directory.CreateDirectory(Path.GetDirectoryName(path));\n        \n        try\n        {\n            File.WriteAllText(path, "Some data to save.");\n            Console.WriteLine("Data saved successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Failed to save data: " + ex.Message);\n        }\n    }\n}