using System;\nusing System.Text;\n\npublic static string DecodeHexString(string hexString)\n{\n    if ((hexString.Length % 2) != 0)\n        throw new ArgumentException("Input must have an even number of characters.", nameof(hexString));\n\n    var bytes = new byte[hexString.Length / 2];\n    for (int i = 0; i < bytes.Length; i++)\n    {\n        // Convert each pair of hex digits to a byte\n        bytes[i] = Convert.ToByte(hexString.Substring(i * 2, 2), 16);\n    }\n\n    // Decode the byte array as Unicode string\n    return Encoding.Unicode.GetString(bytes);\n}