using System;\nusing System.Xml;\n\nclass Program {\n    static void Main() {\n        XmlDocument xDoc = new XmlDocument();\n        XmlElement root = xDoc.CreateElement("xml");\n        \n        XmlElement authorElement = xDoc.CreateElement("author");\n        authorElement.InnerText = "Joe the Magnificent";\n        \n        XmlElement titleElement = xDoc.CreateElement("title");\n        titleElement.InnerText = "Joe Goes Home";\n\n        root.AppendChild(authorElement);\n        root.AppendChild(titleElement);\n\n        xDoc.AppendChild(root);\n\n        string xmlString = xDoc.OuterXml;\n        Console.WriteLine(xmlString);\n\n        // This XML string can now be sent to another machine\n    }\n}