using System;\n\npublic class Program\n{\n    public static void Main()\n    {\n        Console.WriteLine("Enter a value (options are 'red', 'green', 'blue'):");\n        string input = Console.ReadLine();\n\n        if (IsValidColor(input))\n        {\n            Console.WriteLine($"You entered a valid color: {input}");\n        }\n        else\n        {\n            Console.WriteLine("Invalid color. Please enter either 'red', 'green', or 'blue'.");\n        }\n    }\n\n    private static bool IsValidColor(string value)\n    {\n        switch (value.ToLower())\n        {\n            case "red":\n            case "green":\n            case "blue":\n                return true;\n            default:\n                return false;\n        }\n    }\n}