using System;\nusing System.Globalization;\n\nclass Program {\n    static void Main() {\n        string input = "24/5/2009 14:00";\n        DateTime dateTime = DateTime.ParseExact(input, \n                                                  "d/M/yyyy HH:mm", // Use HH for 24-hour time\n                                                   CultureInfo.InvariantCulture);\n        string result = dateTime.ToString("M/d/yyyy h:mm tt"); // Convert to 12-hour format with AM/PM\n        \n        Console.WriteLine(result); // Output will be: 5/24/2009 2:00 PM\n    }\n}