// Step 1: Define a metadata interface\npublic interface IApplicationMetadata\n{\n    string Application { get; }\n}\n\n// Step 2: Modify the ImportCollection to use Metadata\n[ImportMany]\nprivate Lazy<IApplication, IApplicationMetadata>[] _applicationsWithMetadata { get; set; }\n\n// Step 3: Query using the Metadata directly\npublic IApplication GetApplication(string applicationName)\n{\n    var app = _applicationsWithMetadata.FirstOrDefault(a => a.Metadata.Application == applicationName);\n    return app?.Value;\n}