protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initial state or other logic\n    }\n\n    // Server-side validation control disablement based on checkbox state\n    bool isCallWithCCChecked = chkCallWithCC.Checked;\n    rfvCreditCardNumber.Enabled = !isCallWithCCChecked;\n    cvCreditCardNumber.Enabled = !isCallWithCCChecked;\n}\n\nprotected void cvCreditCardNumber_ServerValidate(object source, ServerValidateEventArgs args)\n{\n    // Custom validation logic for credit card number\n    if (args.Value.Length != 16) // Example: Credit card length should be 16\n    {\n        args.IsValid = false;\n    }\n}