public bool ValidateSource(Source srcToValidate, ModelStateDictionary modelState)\n    {\n        if (!string.IsNullOrWhiteSpace(srcToValidate.Name))\n        {\n            if (srcToValidate.Name.Trim().Length == 0)\n                modelState.AddModelError("Name", "Invalid name.");\n        }\n        else\n            modelState.AddModelError("Name", "Name is required.");\n\n        if (!string.IsNullOrWhiteSpace(srcToValidate.Url))\n        {\n            if (srcToValidate.Url.Trim().Length == 0)\n                modelState.AddModelError("Url", "Invalid URL.");\n        }\n        else\n            modelState.AddModelError("Url", "URL is required.");\n\n        // Additional validation logic, e.g., database checks\n        if (_repository.CheckIfNameExists(srcToValidate.Name))\n            modelState.AddModelError("Name", "Name already exists.");\n\n        return !modelState.IsValid;\n    }