// Example of setting up cookie options in ASP.NET MVC\n   FormsAuthenticationTicket ticket = new FormsAuthenticationTicket(1, username, DateTime.Now, DateTime.Now.AddMinutes(30), rememberMe);\n   string encryptedTicket = FormsAuthentication.Encrypt(ticket);\n   HttpCookie authCookie = new HttpCookie(FormsAuthentication.FormsCookieName, encryptedTicket) { Secure = true };\n   Response.Cookies.Add(authCookie);