protected void ScriptManager1_AsyncPostBackError(object sender, AsyncPostBackErrorEventArgs e)\n{\n    // Log or handle the error.\n    string errorMessage = "Async postback error: " + e.Exception.Message;\n    System.Diagnostics.Debug.WriteLine(errorMessage);\n    \n    // Optionally, you can customize how to handle errors, log them, etc.\n    ScriptManager1.AsyncPostBackErrorMessage = errorMessage;\n}\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        ScriptManager1.AsyncPostBackError += new EventHandler<AsyncPostBackErrorEventArgs>(ScriptManager1_AsyncPostBackError);\n    }\n}