using System.Web.Security;\n\npublic void RenameRole(string oldRoleName, string newRoleName)\n{\n    // Check if the new role already exists\n    if (Roles.RoleExists(newRoleName))\n    {\n        throw new InvalidOperationException("The new role name already exists.");\n    }\n\n    // Check if the old role exists\n    if (!Roles.RoleExists(oldRoleName))\n    {\n        throw new InvalidOperationException("The old role does not exist.");\n    }\n\n    // Create a new role with the desired name\n    Roles.CreateRole(newRoleName);\n\n    // Move all users from the old role to the new one\n    foreach (string user in Roles.GetUsersInRole(oldRoleName))\n    {\n        Roles.AddUserToRole(user, newRoleName);\n    }\n\n    // Remove the old role\n    Roles.DeleteRole(oldRoleName, true);  // Second parameter is 'true' to allow deletion even if there are no users in it\n}