using System.IO;\n   using iTextSharp.text.pdf;\n\n   public byte[] CombinePdfs(string[] pdfFiles)\n   {\n       using (MemoryStream msOutput = new MemoryStream())\n       {\n           PdfCopy pdfCopy = new PdfCopy(new Document(), msOutput);\n           pdfCopy.Open();\n\n           foreach (string file in pdfFiles)\n           {\n               // Open the PDF document to be copied\n               using (PdfReader reader = new PdfReader(file))\n               {\n                   int numberOfPages = reader.NumberOfPages;\n                   for (int页码 = 1; 页码 <= numberOfPages; 页码++)\n                   {\n                       pdfCopy.AddPage(pdfCopy.GetImportedPage(reader, 页码));\n                   }\n               }\n           }\n\n           // Close the document\n           pdfCopy.Close();\n           return msOutput.ToArray();\n       }\n   }