// Example method to adjust hyperlinks target attribute programmatically in your CodeBehind\nprotected void AdjustHyperlinkTargets(Control control)\n{\n    foreach (Control child in control.Controls)\n    {\n        if (child is HyperLink)\n        {\n            ((HyperLink)child).Target = "rightFrame";\n        }\n        else\n        {\n            AdjustHyperlinkTargets(child);\n        }\n    }\n}\n\n// Call this method from Page_Load or wherever appropriate, passing the container of your links (e.g., a panel or page itself)\nAdjustHyperlinkTargets(this.Page);